import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import "leaflet-control-geocoder/dist/Control.Geocoder.css";
import "leaflet-control-geocoder/dist/Control.Geocoder.js";
import imag from './icons/ups.png'
import L from "leaflet";
import Navbar from "../Navi";
import Footer from '../Footer';
import './styles.css'

const Leaf = () => {
  const position = [13.0418, 80.2049];
  const [markerData, setMarkerData] = useState([]);

  // Function to fetch data from the API
  const fetchDataFromApi = async () => {
    try {
      const response = await fetch('http://172.16.9.238:8080/rfid/getloc/13'); // Replace with your API URL
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setMarkerData(data);
    } catch (error) {
      console.error('Error fetching data from the API:', error);
    }
  };

  useEffect(() => {
    fetchDataFromApi();
  }, []);

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div className="App">
        <MapContainer center={position} zoom={10} scrollWheelZoom={false}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {markerData.map((dataItem, index) => (
            <Marker
              key={index}
              position={[dataItem.lat, dataItem.lon]}
            >
              <Popup>
                <div>
                  <h4>AWB No: {dataItem.awbNo}</h4>
                  <p>Product: {dataItem.product}</p>
                  <p>Dispatch Status: {dataItem.dispatchStatus}</p>
                </div>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
        {/* Add a table or any other UI element to display the data */}
        <div className="w-50 flex justify-end mr-24 -translate-y-72 box-border w-50">
          <div className="overflow-x-auto">
            <table className="table">
              <thead>
                <tr>
                  <th></th>
                  <th>AWB No</th>
                  <th>Product</th>
                  <th>Dispatch Status</th>
                </tr>
              </thead>
              <tbody>
                {markerData.map((dataItem, index) => (
                  <tr key={index}>
                    <th>{index + 1}</th>
                    <td>{dataItem.awbNo}</td>
                    <td>{dataItem.product}</td>
                    <td>{dataItem.dispatchStatus}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      <div className="mt-24">
        <Footer />
      </div>
    </div>
  );
};

let DefaultIcon = L.icon({
  iconUrl: imag,
  iconSize: [25, 41],
  iconAnchor: [10, 41],
  popupAnchor: [2, -40],
});
L.Marker.prototype.options.icon = DefaultIcon;

export default Leaf;
