import React, { useState, useEffect } from "react";
import ProgressBar from "../Shipping/shipping"; // Import ProgressBar component
import { Link } from "react-router-dom";

function Fetch() {
  // State to store fetched data and selected AWB number
  const [data, setData] = useState([]);
  const [selectedAwb, setSelectedAwb] = useState("");

  // Fetch data from the API when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://172.22.81.182:8080/rfid/getall");
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        const actualData = await response.json();
        setData(actualData);
      } catch (error) {
        console.error("Error fetching data:", error.message);
      }
    };

    fetchData();
  }, []);

  // Handle the "Track" button click by setting the selected AWB number
  const handleTrackClick = (awb) => {
    setSelectedAwb(awb);
  };

  return (
    <div>
      {data.map((item, index) => (
        <div key={index}>
          <div className="overflow-x-auto">
            <table className="table table-zebra mx-auto w-1/2">
              <thead>
                <tr>
                  <th className="text-center w-1/4">Name</th>
                  <th className="text-center w-1/4">Brand</th>
                  <th className="text-center w-1/4">Price</th>
                  <th className="text-center w-1/4">Rating</th>
                  <th className="text-center w-1/4">Tracking</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td className="text-center align-middle w-1/4">{item.awb}</td>
                  <td className="text-center align-middle w-1/4">{item.sender_data}</td>
                  <td className="text-center align-middle w-1/4">{item.dimensions}</td>
                  <td className="text-center align-middle w-1/4">{item.destination_details}</td>
                  <td>
                    <Link to={`/progress/${item.awb}`}>
                      <button
                        className="btn btn-neutral"
                        onClick={() => handleTrackClick(item.awb)}
                      >
                        Track
                      </button>
                    </Link>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      ))}
      {selectedAwb && <ProgressBar trackingNumber={selectedAwb} />}
    </div>
  );
}

export default Fetch;
