import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams, useLocation } from 'react-router-dom';
function Progress({apiData}){
  
  <div className="progress-bar">
        <div className="progress-labels">
          {labels.map((label) => (
            <div
              key={label}
              className={`progress-label ${apiData.status === label ? 'active' : ''}`}
            >
              {label}
            </div>
          ))}
        </div>
      </div>
      {apiData.status === 'OUTFORDEL' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 rounded-full h-11 w-24 text-black font-semibold mt-5 ml-56 -translate-y-20">
            Track
          </button>
        </Link>
      )}
      <div>
        <h2>Shipping Details</h2>
        <table>
          <tbody>
            <tr>
              <td>AWB:</td>
              <td>{awbNumber}</td>
            </tr>
            <tr>
              <td>Sender Data:</td>
              <td>{sender_data}</td>
            </tr>
            <tr>
              <td>Origin Data:</td>
              <td>{origin_data}</td>
            </tr>
            <tr>
              <td>Destination:</td>
              <td>{destination_data}</td>
            </tr>
          </tbody>
        </table>
      </div>
}

const ProgressBar = () => {

  useEffect(() => {
    // Refresh the page every 30 seconds (adjust as needed)
    const refreshInterval = setInterval(() => {
      window.location.reload(); // Reload the entire page
    }, 3000); // 30 seconds

    return () => {
      // Clear the interval when the component unmounts
      clearInterval(refreshInterval);
    };
  }, []);

  

  return (
    <div className='min-h-screen'>
      <Navbar />
      <Progress data={apiData}/>
      <div className='mt-12'>
        <Footer />
      </div>
    </div>
  );
};

export default ProgressBar;
