import "./styles.css";
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import imag from "./icons/placeholder.png"
import "leaflet-routing-machine/dist/leaflet-routing-machine.css";
import L from "leaflet";
import "leaflet-routing-machine";


import { Icon, divIcon, point } from "leaflet";

// create custom icon
const customIcon = new Icon({
  // iconUrl: "https://cdn-icons-png.flaticon.com/512/447/447031.png",
  iconUrl: imag,
  iconSize: [38, 38] // size of the icon
});

// custom cluster icon
const createClusterCustomIcon = function (cluster) {
  return new divIcon({
    html: `<span class="cluster-icon">${cluster.getChildCount()}</span>`,
    className: "custom-marker-cluster",
    iconSize: point(33, 33, true)
  });
};

// markers
const markers = [
  {
    geocode: [12.971581, 80.043],
    popUp: "Load : 450 Kg"
  },
  {
    geocode: [12.9934, 80.085],
    popUp: "Load : 100 kg"
  },
  {
    geocode: [12.9603, 80.05827],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [13.116670, 80.103081],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [13.0499, 80.2095],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [9.939093, 78.121719],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [9.4533, 77.8024],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [9.9151, 78.1237],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [11.6643, 78.1460],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [9.9195, 78.1193],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [13.067439, 80.237617],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [13.1009, 80.1902],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [13.0355, 80.2305],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [12.9763, 80.1593],
    popUp: "Load : 200 kg"
  },
  {
    geocode: [13.0689, 80.1968],
    popUp: "Load : 300 kg"
  }
];

export default function Leaf() {
  return (
    <div className="flex justify-center content-center">
      <MapContainer center={[12.971581, 80.043]} zoom={13}>
  {/* ... */}
  {/* Your existing TileLayer */}
  {/* ... */}
  <RoutingMachine
    position="topleft"
    plan={L.Routing.plan(
      [
        L.latLng(12.971581, 80.043), // Starting point
        L.latLng(12.9934, 80.085), // Intermediate point(s)
        L.latLng(13.0689, 80.1968), // Ending point
      ],
      {
        addWaypoints: false, // If true, you can add intermediate waypoints by clicking on the map
        draggableWaypoints: false, // If true, you can drag waypoints on the map
        routeWhileDragging: true, // If true, the route updates as you drag waypoints
        geocoder: L.Control.Geocoder.nominatim(), // You can use other geocoders if needed
        routeDragTimeout: 250, // Delay in milliseconds before the route updates while dragging
        autoRoute: true, // If true, the route is calculated automatically when waypoints change
        showAlternatives: true, // If true, alternative routes are shown
      }
    )}
  />
  {/* ... */}
</MapContainer>

    
    </div>
  );
}
