import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams } from 'react-router-dom';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const [shippingData, setShippingData] = useState({}); // State to store shipping data
  const { awbNumber } = useParams();

  useEffect(() => {
    // Fetch API data for the specific awbNumber
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.json()) // Assuming the response is JSON
      .then((data) => {
        // Assuming the data is an object with properties you need
        console.log("Fetched Data:", data);

        // Set the shipping data state with the fetched data
        setShippingData(data);

        // Assuming the API response contains only the plain text "REACHED"
        setApiData({ status: data.status.trim() });
      })
      .catch((error) => {
        console.error('Error fetching data from API:', error);
      });
  }, [awbNumber]);

  const labels = ['DISPATCHED', 'REACHED', 'REACHED HUB 1', 'REACHED HUB 2', 'OUTFORDEL'];

  return (
    <div className='min-h-screen'>
      <Navbar />
      <div className="progress-bar">
        <div className="progress-labels flex">
          {labels.map((label) => (
            <div
              key={label}
              className={`progress-label ${apiData.status === label ? 'active' : ''}`}
            >
              {label}
            </div>
          ))}
        </div>
      </div>
      {apiData.status === 'OUTFORDEL' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 rounded-full h-11 w-24 text-black font-semibold mt-5">
            Track
          </button>
        </Link>
      )}
      <div>
        <h2>Shipping Details</h2>
        <table>
          <tbody>
            <tr>
              <td>AWB:</td>
              <td>{awbNumber}</td>
            </tr>
            <tr>
              <td>Sender Data:</td>
              <td>{shippingData.sender_data}</td>
            </tr>
            <tr>
              <td>Origin Data:</td>
              <td>{shippingData.origin_data}</td>
            </tr>
            <tr>
              <td>Destination:</td>
              <td>{shippingData.destination_data}</td>
            </tr>
          </tbody>
        </table>
      </div>
      <div className='mt-12'>
        <Footer />
      </div>
    </div>
  );
};

export default ProgressBar;
