import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams } from 'react-router-dom';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();

  useEffect(() => {
    // Replace single quotes with backticks to create a template string
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.text())
      .then((data) => {
        // Assuming the API response contains only the plain text "REACHED"
        setApiData({ status: data.trim() });
      })
      .catch((error) => {
        console.error('Error fetching data from API:', error);
      });
  }, [awbNumber]);

  const labels = ['DISPATCHED', 'REACHED', 'REACHED HUB 1', 'REACHED HUB 2', 'OUTFORDEL'];

  return (
    <div className='min-h-screen'>
    <Navbar/>
    <div className="progress-bar">
      <div className="progress-labels flex">
        {labels.map((label) => (
          <div
            key={label}
            className={`progress-label ${apiData.status === label ? 'active' : ''}`}
          >
            {label}
          </div>
        ))}
      </div>
      <div>
      <table className="table table-zebra mx-auto w-1/2">
        <thead className="bg-yellow-400 text-black">
          <tr>
            <th className="text-center w-1/4">Sender Data</th>
            <th className="text-center w-1/4">Origin Data</th>
            <th className="text-center w-1/4">Destination</th>
          </tr>
        </thead>
        <tbody className='tboby'>
          {data.map((item, index) => (
            <tr key={index}>
              <td className="text-center align-middle w-1/4">{item.sender_data}</td> 
              <td className="text-center align-middle w-1/4">{item.origin_data}</td>
              <td className="text-center align-middle w-1/4">{item.destination_data}</td>
              <td>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
      <div className={`progress-bar ${apiData.status}`} />
      {apiData.status === 'OUTFORDEL' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 rounded-full h-11 w-24  text-black font-semibold   mt-5">
            Track
          </button>
        </Link>
      )}
      </div>
      <div className='mt-12'>
        <Footer/>
      </div>
      
    </div>
  );
};

export default ProgressBar;
