import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import "leaflet-control-geocoder/dist/Control.Geocoder.css";
import "leaflet-control-geocoder/dist/Control.Geocoder.js";
import L from "leaflet";
import Navbar from "../Navi";
import Footer from '../Footer';
import './styles.css';

const customIcon = new L.Icon({
  iconUrl: './icons/ups.png',
  iconSize: [38, 38],
});

function Leaf() {
  const [markerPosition, setMarkerPosition] = useState(null); // Initialize as null
  
  useEffect(() => {
    // Fetch data from the API containing latitude and longitude
    const fetchData = async () => {
      try {
        const response = await fetch('http://your-api-url-here'); // Replace with your API URL
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();

        // Update the marker position based on the API data
        setMarkerPosition([data.lat, data.lon]);
      } catch (error) {
        console.error('Error fetching data from the API:', error);
      }
    };

    // Call the fetchData function initially to set the marker position
    fetchData();

    // Cleanup the interval when the component unmounts
    return () => {
      setMarkerPosition(null); // Reset marker position
    };
  }, []);

  return (
    <div>
      <div>
        <Navbar />
      </div>
      <div className="App">
        <MapContainer center={markerPosition || [13.0418, 80.2049]} zoom={10} scrollWheelZoom={false}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {markerPosition && <Marker position={markerPosition} icon={customIcon} />}
        </MapContainer>
      </div>
      <div className="mt-24">
        <Footer />
      </div>
    </div>
  );
}

export default Leaf;
