import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams } from 'react-router-dom';
import props from 'react'
const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();
  const { awb, sender_data, origin_data, destination_data } = props.location?.state || {};

  useEffect(() => {
    // Replace single quotes with backticks to create a template string
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.text())
      .then((data) => {
        // Assuming the API response contains only the plain text "REACHED"
        setApiData({ status: data.trim() });
      })
      .catch((error) => {
        console.error('Error fetching data from API:', error);
      });
  }, [awbNumber]);

  const labels = ['DISPATCHED', 'REACHED', 'REACHED HUB 1', 'REACHED HUB 2', 'OUTFORDEL'];

  return (
    <div className='min-h-screen'>
    <Navbar/>
    <div className="progress-bar">
      <div className="progress-labels flex">
        {labels.map((label) => (
          <div
            key={label}
            className={`progress-label ${apiData.status === label ? 'active' : ''}`}
          >
            {label}
          </div>
        ))}
      </div>
      <div className={`progress-bar ${apiData.status}`} />
      {apiData.status === 'OUTFORDEL' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 rounded-full h-11 w-24  text-black font-semibold   mt-5">
            Track
          </button>
        </Link>
      )}
      </div>
      <div>
      <h2>Shipping Details</h2>
      <table>
        <tbody>
          <tr>
            <td>AWB:</td>
            <td>{awb}</td>
          </tr>
          <tr>
            <td>Sender Data:</td>
            <td>{sender_data}</td>
          </tr>
          <tr>
            <td>Origin Data:</td>
            <td>{origin_data}</td>
          </tr>
          <tr>
            <td>Destination:</td>
            <td>{destination_data}</td>
          </tr>
        </tbody>
      </table>
    </div>
      <div className='mt-12'>
        <Footer/>
      </div>
      
    </div>
  );
};

export default ProgressBar;
