import "leaflet-routing-machine/dist/leaflet-routing-machine.css";
import L from "leaflet";
import "leaflet-routing-machine";

export default function Leaf() {
  return (
    <div className="flex justify-center content-center">
      <MapContainer center={[12.971581, 80.043]} zoom={13}>
  {/* ... */}
  {/* Your existing TileLayer */}
  {/* ... */}
  <RoutingMachine
    position="topleft"
    plan={L.Routing.plan(
      [
        L.latLng(12.971581, 80.043), // Starting point
        L.latLng(12.9934, 80.085), // Intermediate point(s)
        L.latLng(13.0689, 80.1968), // Ending point
      ],
      {
        addWaypoints: false, // If true, you can add intermediate waypoints by clicking on the map
        draggableWaypoints: false, // If true, you can drag waypoints on the map
        routeWhileDragging: true, // If true, the route updates as you drag waypoints
        geocoder: L.Control.Geocoder.nominatim(), // You can use other geocoders if needed
        routeDragTimeout: 250, // Delay in milliseconds before the route updates while dragging
        autoRoute: true, // If true, the route is calculated automatically when waypoints change
        showAlternatives: true, // If true, alternative routes are shown
      }
    )}
  />
  {/* ... */}
</MapContainer>

    
    </div>
  );
}
