import React, { useState, useEffect } from 'react';
import './ProgressBar.css';
import Navbar from '../Nav';
import Footer from '../Footer';
import { Link, useParams } from 'react-router-dom';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });
  const { awbNumber } = useParams();

  // Use local state for shipping details
  const [shippingDetails, setShippingDetails] = useState({
    awb: '',
    sender_data: '',
    origin_data: '',
    destination_data: '',
  });

  useEffect(() => {
    // Fetch API data
    fetch(`http://172.22.81.182:8080/rfid/getstat/${awbNumber}`)
      .then((response) => response.text())
      .then((data) => {
        // Assuming the API response contains only the plain text "REACHED"
        setApiData({ status: data.trim() });
      })
      .catch((error) => {
        console.error('Error fetching data from API:', error);
      });

    // Fetch shipping details from props.location.state (if available)
    if (props.location?.state) {
      setShippingDetails(props.location.state);
    }
  }, [awbNumber]);

  const labels = ['DISPATCHED', 'REACHED', 'REACHED HUB 1', 'REACHED HUB 2', 'OUTFORDEL'];

  return (
    <div className='min-h-screen'>
      <Navbar />
      <div className="progress-bar">
        {/* Progress bar and labels */}
      </div>
      {apiData.status === 'OUTFORDEL' && (
        <Link to='/map'>
          <button className="bg-amber-400 hover:bg-amber-300 rounded-full h-11 w-24 text-black font-semibold mt-5">
            Track
          </button>
        </Link>
      )}
      <div>
        <h2>Shipping Details</h2>
        <table>
          <tbody>
            <tr>
              <td>AWB:</td>
              <td>{shippingDetails.awb}</td>
            </tr>
            <tr>
              <td>Sender Data:</td>
              <td>{shippingDetails.sender_data}</td>
            </tr>
            <tr>
              <td>Origin Data:</td>
              <td>{shippingDetails.origin_data}</td>
            </tr>
            <tr>
              <td>Destination:</td>
              <td>{shippingDetails.destination_data}</td>
            </tr>
          </tbody>
        </table>
      </div>
      <div className='mt-12'>
        <Footer />
      </div>
    </div>
  );
};

export default ProgressBar;
